CREATE TABLE IF NOT EXISTS `aweb_globalauth` ( `id` int(11) NOT NULL auto_increment, `username` varchar(64) NOT NULL default '', `password` varchar(32) NOT NULL default '', `first_name` text NULL, `last_name` text NULL, `email` text NULL, `last_login` timestamp NULL, `sourceid` INT(10) UNSIGNED NOT NULL DEFAULT '0', `sourceupdated` DATETIME NULL DEFAULT NULL, `productset` TEXT NULL DEFAULT NULL, PRIMARY KEY  (`id`), KEY  (`username`) )  ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE = utf8_general_ci;
CREATE TABLE IF NOT EXISTS `aweb_authinfo` ( `id` int(10) unsigned NOT NULL auto_increment, `absid` int(10) unsigned NOT NULL default '0', `product` varchar(64) NOT NULL default '', `portable` tinyint(1) NOT NULL default '1', PRIMARY KEY  (`id`), KEY `absid` (`absid`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 DEFAULT COLLATE = utf8_general_ci;
